import React from 'react';
import styles from '../App/App.module.css';
import Tracklist from '../Tracklist/Tracklist';

function Playlist( {selectedTracks, savePlaylist, playlistName, playlistTracks, setPlaylistName } ) {
    /* const mockPlaylist = [
        { id: 1, name: "Track 1", artist: "Artist 1" },
        { id: 2, name: "Track 2", artist: "Artist 2" },
    ]; */

    return (
        <div className={styles.Playlist}>
            <input 
                type="text"
                value={playlistName}
                onChange={(e) => setPlaylistName(e.target.value)}
            />
            <ul>
                {selectedTracks.map(track => (
                    <li key={track.id}>{track.name} by {track.artist} ({track.album})</li>
                ))}
            </ul>
            {selectedTracks.length > 0 && (
                <button onClick={savePlaylist}>Save to Spotify</button>
            )}
        </div>
    );
};

export default Playlist;
